CSharpSnippetActionItem.CSharpCodeSnippet:
var account = BindingSource.Current as IAccount;
if (account == null) return;
lstbxBackOffices.Items.Clear();
lstbxAccountingEntities.Items.Clear();
var fls = Sage.Platform.Application.ApplicationContext.Current.Services.Get<Sage.Platform.Security.IFieldLevelSecurityService>(true);
if (fls != null)
{
    var access = fls.GetAccessForProperty(account, "ERPTradingAccount.FinanceLimit");
    QFSLXCurrency.ReadOnly = (access == Sage.Platform.Security.FieldAccess.ReadOnly);

	var accessProperty = fls.GetAccessForProperty(account, "ERPTradingAccount.FinanceLimitBaseAmount");
    QFSLXCurrency2.ReadOnly = (accessProperty == Sage.Platform.Security.FieldAccess.ReadOnly);
}

if (!string.IsNullOrEmpty(account.ErpExtId) ||
    account.SyncStatus == Saleslogix.Integration.BOE.Common.Constants.SyncStatus.Published ||
    account.SyncStatus == Saleslogix.Integration.BOE.Common.Constants.SyncStatus.AwaitingACK)
{
    var backOffice = Sage.Platform.EntityFactory.GetRepository<IBackOffice>().FindFirstByProperty("LogicalId", account.ErpLogicalId);
    if (backOffice != null)
    {
        lstbxBackOffices.Attributes.Add("disabled", "disabled");
        lstbxAccountingEntities.Attributes.Add("disabled", "disabled");
        lstbxBackOffices.Items.Add(new ListItem { Text = backOffice.BackOfficeName, Value = backOffice.BackOfficeName });
        lstbxAccountingEntities.Text = account.ErpAccountingEntityId;
        lstbxAccountingEntities.Items.Add(new ListItem { Text = account.ErpAccountingEntityId, Value = account.ErpAccountingEntityId });
    }
    return;
}
lstbxBackOffices.Attributes.Remove("disabled");
lstbxAccountingEntities.Attributes.Remove("disabled");

lstbxBackOffices.Items.Add("");
lstbxAccountingEntities.Items.Add("");
var backOffices = Sage.SalesLogix.BackOffice.Rules.GetActiveBackOffices("Account");
foreach (var backOffice in backOffices)
{
    lstbxBackOffices.Items.Add(new ListItem { Text = backOffice.BackOfficeName, Value = backOffice.Id.ToString() });
    if (account.ErpLogicalId == backOffice.LogicalId)
    {
        foreach (var backOffAccEntity in backOffice.BackOfficeAccountingEntities)
        {
            lstbxAccountingEntities.Items.Add(backOffAccEntity.AcctEntityExtId);
        }
        lstbxBackOffices.SelectedValue = backOffice.Id.ToString();
        lstbxAccountingEntities.SelectedValue = account.ErpAccountingEntityId;
    }
    else if (lstbxBackOffices.Items.Count != 0 && string.IsNullOrEmpty(lstbxBackOffices.SelectedValue))
    {
        lstbxBackOffices.Items[0].Selected = true;
        if (lstbxAccountingEntities.Items.Count != 0 && string.IsNullOrEmpty(lstbxAccountingEntities.SelectedValue))
            lstbxAccountingEntities.Items[0].Selected = true;
    }
}

var account = BindingSource.Current as IAccount;
if (!string.IsNullOrEmpty(account.ErpExtId)) return;
var backOffice = Sage.Platform.EntityFactory.GetById<IBackOffice>(lstbxBackOffices.SelectedValue);
if (backOffice != null)
{
	account.ErpLogicalId = backOffice.LogicalId;
	lstbxAccountingEntities.Items.Clear();
	lstbxAccountingEntities.Items.Add("");
	foreach (var backOffAccEntity in backOffice.BackOfficeAccountingEntities)
	{
	    lstbxAccountingEntities.Items.Add(new ListItem {Text = backOffAccEntity.AcctEntityExtId, Value = backOffAccEntity.AcctEntityExtId, Selected = backOffice.BackOfficeAccountingEntities.Count == 1});
		if (backOffice.BackOfficeAccountingEntities.Count == 1)
	    {
	        account.ErpAccountingEntityId = backOffAccEntity.AcctEntityExtId;
	    }
	}
	var accList = lstbxAccountingEntities.Items.Cast<ListItem>().OrderBy(item => item.Text).ToList();
	lstbxAccountingEntities.Items.Clear();
	foreach (ListItem accListItem in accList)
	{
	    lstbxAccountingEntities.Items.Add(accListItem);
	}
}

var account = BindingSource.Current as IAccount;
if (!string.IsNullOrEmpty(account.ErpExtId)) return;
account.ErpAccountingEntityId = lstbxAccountingEntities.SelectedValue;
