CSharpSnippetActionItem.CSharpCodeSnippet:
var contact = (IContact) BindingSource.Current;

QFLocationCodeList.Items.Clear();
List<Sage.SalesLogix.Web.EnabledLanguage> cultures = Sage.SalesLogix.PickLists.PickListHelper.GetLanguageList<Sage.SalesLogix.Web.EnabledLanguage>(Sage.SalesLogix.PickLists.PickListHelper.LanguageListTypes.Cultures);
cultures.ForEach(ci => QFLocationCodeList.Items.Add(new ListItem(text: ci.DisplayText, value: ci.CultureCode.ToLowerInvariant())));
QFLocationCodeList.Items.Insert(0, new ListItem(text: string.Empty, value: null));
if (!string.IsNullOrWhiteSpace(contact.LocationCode)) {
	QFLocationCodeList.SelectedValue = contact.LocationCode.Trim();
} else {
	QFLocationCodeList.SelectedIndex = 0;
}
