public virtual System.Int32 DaysOpen
{
    get
    {
           int days =0;
           DateTime openDate;
		   DateTime closeDate;
           try
           {
               if(this.DateOpened != null)
			   {    
			      openDate = (DateTime)this.DateOpened;
			   }
			   else
			   {
				  openDate = (DateTime)this.CreateDate;
			   }

			   if (this.Status == "Open") 
			   {
                   closeDate = System.DateTime.UtcNow; 
			   }
			   else //All other status
               {
                    if(this.ActualClose != null)
				    {    
					     closeDate = (DateTime)this.ActualClose;
				    }
					else
					{
					     closeDate = System.DateTime.UtcNow;
					}
               }

			   TimeSpan timeSpan = new TimeSpan(closeDate.Ticks - openDate.Ticks);
               days =(int) timeSpan.TotalDays;
           }
           catch
           {
               days = 0;
           }
           return days;
    }
}

public virtual System.Int32 DaysSinceLastActivity
{
	get
    {
     int days = 0;
     DateTime modDate;
	 DateTime currTime;

     try
      {
          if (this.ModifyDate != null)
          {
			 currTime = System.DateTime.UtcNow;
             modDate = (DateTime)this.ModifyDate;
             TimeSpan ts = new TimeSpan(currTime.Ticks - modDate.Ticks);
             days = (int)ts.Days;
          }
      }
      catch
      {
         days = 0;
      }
      return days;
     }
}
