var quoteItem = BindingSource.Current as IQuoteItem;
var clientContextService = PageWorkItem.Services.Get<Sage.Platform.WebPortal.Services.ClientContextService>();
if (clientContextService != null)
{
    // This is used to load Discount Charge Items
	clientContextService.CurrentContext["QUOTEITEMID"] = quoteItem.Id as string;
}
var quote = GetParentEntity() as IQuote;
quoteItem.Quote = quoteItem.Quote ?? quote;
var closed = quote.IsClosed();
bool isAdHocProduct = false;
if (DialogService != null)
{
	isAdHocProduct = DialogService.DialogParameters.ContainsKey("AdHocProduct");
	DialogService.DialogParameters.Remove("AdHocProduct");
}
var mappingService = Sage.Platform.Application.ApplicationContext.Current.Services.Get<Sage.Platform.SData.IAppIdMappingService>(true);
bool isIntegrated = mappingService.IsIntegrationEnabled();
bool isQuotePricingEnabled=mappingService.IsLocalCRMPricingEnabled("Quote");
bool isIntegratedProduct=(isIntegrated && !isQuotePricingEnabled) && (quoteItem.LineType != "FreeText" && !isAdHocProduct);
tabErpItemDetails.Panels[0].Visible = !isIntegrated || isQuotePricingEnabled;
tabErpItemDetails.Panels[1].Visible = isIntegrated && !isQuotePricingEnabled;
tabErpItemDetails.Panels[2].Visible = (!isIntegrated || isQuotePricingEnabled) && (quoteItem.LineType != "FreeText" && !isAdHocProduct);
tabErpItemDetails.Panels[3].Visible = isIntegratedProduct;
tabErpItemDetails.Panels[4].Visible = isIntegratedProduct;
tabErpItemDetails.Panels[5].Visible = isIntegratedProduct;
tabErpItemDetails.Panels[6].Visible = isIntegratedProduct;

txtErpLineNumber.Parent.Visible = isIntegrated;
