txtLineNumber.Parent.Visible = !isIntegrated;
txtLineNumber_lbl.Parent.Visible = !isIntegrated;

var isMCEnabled = Sage.SalesLogix.BusinessRules.BusinessRuleHelper.IsMultiCurrencyEnabled();
curDocCalculatedPrice.Visible = isMCEnabled;
