curDocExtendedPrice.Visible = isMCEnabled;
curDocExtendedPrice_lbl.Visible = isMCEnabled;
curErpDocCalculatedPrice.Visible = isMCEnabled;
curErpDocCalculatedPrice_lbl.Visible = isMCEnabled;
curErpDocExtendedPrice.Visible = isMCEnabled;
curErpDocExtendedPrice_lbl.Visible = isMCEnabled;

var erpQuote = !string.IsNullOrEmpty(quote.ErpExtId);
curPrice.ReadOnly = erpQuote;

if (closed)
{
    txtDescription.Enabled = false;
    txtSKU.Enabled = false;
    pklFamily.Enabled = false;
    txtDiscount.Enabled = false;
    curCalculatedPrice.Enabled = false;
    curErpCalculatedPrice.Enabled = false;
