	pklFamily.Enabled = false;
	pklIncoTerm.Enabled = false;
	chkFixedPrice.Enabled = false;
	chkRushRequest.Enabled = false;
	dteRequiredDelivery.Enabled = false;
	lueShipTo.Enabled = false;
	dtePromoisedShipDate.Enabled = false;
	chkDropShip.Enabled = false;
	dtePromisedDelivery.Enabled = false;
    btnCancel.Text = GetLocalResourceObject("Closed_Caption").ToString();
}
else if (isAdHocProduct || quoteItem.LineType == "FreeText")
{
    quoteItem.LineType = "FreeText";
    quoteItem.Quantity = quoteItem.Quantity ?? 1;
    curPrice.ReadOnly = false;
	curErpPrice.Visible = true;
