var quoteItem = BindingSource.Current as IQuoteItem;
var quote = GetParentEntity() as IQuote;
lstbxPriceLevel.Items.Clear();
if (quoteItem.Product != null)
{
    lstbxPriceLevel.Items.Add(string.Empty);
    foreach (var priceLevel in quoteItem.Product.ProductProgram)
    {
        var item = new ListItem {Text = priceLevel.Program, Value = priceLevel.Program};
        lstbxPriceLevel.Items.Add(item);
    }
    lstbxPriceLevel.SelectedValue = quoteItem.Program;
}

QFSDataGrid.ConditionalWhere:
function() {
	var clientContextService = Sage.Services.getService('ClientContextService');
	var itemId = clientContextService != null && clientContextService.containsKey('QUOTEITEMID') ? clientContextService.getValue("QUOTEITEMID") : null;
	return dojo.string.substitute("QuoteItem.Id eq '${0}'", [itemId]);
}

CSharpSnippetActionItem.CSharpCodeSnippet:
IQuoteItem quoteItem = BindingSource.Current as IQuoteItem;
var quoteItemChangeState = quoteItem as Sage.Platform.ChangeManagement.IChangedState;
var shouldSave = true;
if (quoteItemChangeState != null)
{
    Sage.Platform.ChangeManagement.ChangeSet changes = quoteItemChangeState.GetChangedState();
    if (changes.HasMemberChange("Quantity") || changes.HasMemberChange("UnitOfMeasure"))
    {
        quoteItem.Quote.SyncStatus = Saleslogix.Integration.BOE.Common.Constants.SyncStatus.ChangesPending;
        shouldSave = false;
        quoteItem.Quote.Save();
    }
}
if (shouldSave)
{
    quoteItem.Save();
}
