{
	  string contactId = string.Empty;
    if (isIntegrationEnabled)
    {
        if (salesOrder.ErpBillTo != null) contactId = salesOrder.ErpBillTo.Id.ToString();
    }
    else
    {
        if (salesOrder.BillingContact != null) contactId = salesOrder.BillingContact.Id.ToString();
    }
	  if (string.IsNullOrEmpty(contactId)) return;
    var billingAddresses =
        Sage.Platform.EntityFactory.GetRepository<IAddress>().FindByProperty(
            "EntityId", contactId);
    if (billingAddresses != null)
    {
        foreach (IAddress address in billingAddresses)
        {
            var billingItem = new ListItem {Text = address.Description, Value = address.Id.ToString()};
            if (!selected)
            {
                billingItem.Selected = Equals(address.Description, salesOrder.BillingAddress.Description);
                selected = billingItem.Selected;
            }
            lstbxBillingAddress.Items.Add(billingItem);
        }
    }
