if(salesOrder.AccountManager != null) {
	lkpUserWareHouse.SeedValue = salesOrder.AccountManager.Id.ToString ();
	lkpSourceWareHouse.SeedValue = salesOrder.AccountManager.Id.ToString (); 			
}

if (salesOrder.UserWareHouse != null) {
    lkpUserWareHouse.LookupResultValue = salesOrder.UserWareHouse;
}
if (salesOrder.SourceSLXSite != null) {
    lkpSourceWareHouse.LookupResultValue = salesOrder.SourceSLXSite ;
}

if (salesOrder.OrderType == "Transfer Order" || salesOrder.OrderType == "Inventory Order")
{
	lkpSourceWareHouse.Visible = true;
	lkpSourceWareHouse_lbl.Visible = true;
}
else
{
	lkpSourceWareHouse.Visible = false;
	lkpSourceWareHouse_lbl.Visible = false;
}

if (salesOrder.OrderType == "Sales Order" )
{
	txtDiscount.Visible = true;
	txtDiscount_lbl.Visible = true;
}
else
{
	txtDiscount.Visible = false;
	txtDiscount_lbl.Visible = false;
}

//disable the submit order button if order already submitted
if(salesOrder.Status == "Transmitted to Accounting") {
	cmdSubmitOrder.Enabled = false;
} else {
	cmdSubmitOrder.Enabled = true;
}

    // User Warehouse OnChangeAction
    Sage.Entity.Interfaces.IUSERWHSE _UserWareHouse = Sage.Platform.EntityFactory.GetById<Sage.Entity.Interfaces.IUSERWHSE>(lkpUserWareHouse.LookupResultValue.ToString());
    Sage.Entity.Interfaces.ISalesOrder salesorder = this.BindingSource.Current as Sage.Entity.Interfaces.ISalesOrder ;

    if (salesorder.UserWHSEID != _UserWareHouse.Siteid) {
         salesorder.UserWHSEID = _UserWareHouse.Siteid;
        //salesorder.UserWareHouse = _UserWareHouse.SLXSite;
        //salesorder.Save();

        //remove all products that don't match the warehouse
        ArrayList toRemove = new ArrayList();

        foreach (Sage.Entity.Interfaces.ISalesOrderItem item in salesorder.SalesOrderItems) {
            if (item.Product == null) {
                //product not found, remove item
                toRemove.Add(item);
            } else {
                if (item.Product.WarehouseID != _UserWareHouse.SLXSite.SiteCodeId) {
                    toRemove.Add(item);
                }
            }
        }

        foreach (Sage.Entity.Interfaces.ISalesOrderItem item in toRemove) {
            salesorder.SalesOrderItems.Remove(item);
            item.Delete();
        }
        salesorder.Save();

        //for a salesorder, add products from the stock card
        if (salesorder.OrderType == "Sales Order") {
            salesorder.AddStockcardProducts();
        }
        salesorder.Save();

        ScriptManager.RegisterStartupScript(this, GetType(), "refresh", "window.setTimeout('window.location.reload(true);',50);", true);
        //Response.Redirect(Request.Url.ToString());
	}

