CSharpSnippetActionItem.CSharpCodeSnippet:
var orderItem = BindingSource.Current as ISalesOrderItem;
var clientContextService = PageWorkItem.Services.Get<Sage.Platform.WebPortal.Services.ClientContextService>();
if (clientContextService != null)
{
    // This is used to load Discount Charge Items
    clientContextService.CurrentContext["SOITEMID"] = orderItem.Id as string;
}
var salesOrder = GetParentEntity() as ISalesOrder;
orderItem.SalesOrder = orderItem.SalesOrder ?? salesOrder;
var closed = salesOrder.IsClosed();
bool isAdHocProduct = false;
if (DialogService != null)
{
	isAdHocProduct = DialogService.DialogParameters.ContainsKey("AdHocProduct");
	DialogService.DialogParameters.Remove("AdHocProduct");
}
var mappingService = Sage.Platform.Application.ApplicationContext.Current.Services.Get<Sage.Platform.SData.IAppIdMappingService>(true);
bool isIntegrated = mappingService.IsIntegrationEnabled();
bool isSalesOrderPricingEnabled=mappingService.IsLocalCRMPricingEnabled("SalesOrder");
bool isIntegratedProduct=(isIntegrated && !isSalesOrderPricingEnabled) && (orderItem.LineType != "FreeText" && !isAdHocProduct);
tabErpItemDetails.Panels[0].Visible = !isIntegrated || isSalesOrderPricingEnabled;
tabErpItemDetails.Panels[1].Visible = isIntegrated && !isSalesOrderPricingEnabled;
tabErpItemDetails.Panels[2].Visible = (!isIntegrated || isSalesOrderPricingEnabled) && (orderItem.LineType != "FreeText" && !isAdHocProduct);
tabErpItemDetails.Panels[3].Visible = isIntegratedProduct;
tabErpItemDetails.Panels[4].Visible = isIntegratedProduct;
tabErpItemDetails.Panels[5].Visible = isIntegratedProduct;
tabErpItemDetails.Panels[6].Visible = isIntegratedProduct;

txtErpLineNumber.Parent.Visible = isIntegrated;
txtErpLineNumber_lbl.Parent.Visible = isIntegrated;
txtLineNumber.Parent.Visible = !isIntegrated;
txtLineNumber_lbl.Parent.Visible = !isIntegrated;

var isMCEnabled = Sage.SalesLogix.BusinessRules.BusinessRuleHelper.IsMultiCurrencyEnabled();
curDocCalculatedPrice.Visible = isMCEnabled;
curDocCalculatedPrice_lbl.Visible = isMCEnabled;
curDocExtendedPrice.Visible = isMCEnabled;
curDocExtendedPrice_lbl.Visible = isMCEnabled;
curErpDocCalculatedPrice.Visible = isMCEnabled;
curErpDocCalculatedPrice_lbl.Visible = isMCEnabled;
curErpDocExtendedPrice.Visible = isMCEnabled;
curErpDocExtendedPrice_lbl.Visible = isMCEnabled;

var erpSalesOrder = !string.IsNullOrEmpty(salesOrder.ErpExtId);
curPrice.ReadOnly = erpSalesOrder;

if (closed)
{
    txtDescription.Enabled = false;
    txtSKU.Enabled = false;
    pklFamily.Enabled = false;
    txtDiscount.Enabled = false;
    curCalculatedPrice.Enabled = false;
    curErpCalculatedPrice.Enabled = false;
    numQuantity.Enabled = false;
	numErpQuantity.Enabled = false;
	lueUnitOfMeasure.Enabled = false;
	lstbxPriceLevel.Enabled = false;
	pklFamily.Enabled = false;
	pklIncoTerm.Enabled = false;
	chkFixedPrice.Enabled = false;
	chkRushRequest.Enabled = false;
	chkBackOrdered.Enabled = false;
	chkSubstituteItem.Enabled = false;
	dteRequiredDelivery.Enabled = false;
	lueShipTo.Enabled = false;
	chkDropShip.Enabled = false;
	chkPartialShipAllowed.Enabled = false;
	dtePromisedDelivery.Enabled = false;
	txtPOReference.Enabled = false;
	dtePromisedShipDate.Enabled = false;
	btnCancel.Text = GetLocalResourceObject("Closed_Caption").ToString();
}
else if (isAdHocProduct || orderItem.LineType == "FreeText")
{
    orderItem.LineType = "FreeText";
	orderItem.Quantity = orderItem.Quantity ?? 1;
    curPrice.ReadOnly = false;
	curErpPrice.Visible = true;
	curErpPrice_lbl.Visible = true;
    txtSKU.ReadOnly = false;
    pklFamily.ReadOnly = false;
    txtDescription.ReadOnly = false;
    txtProductName.ReadOnly = false;
}
else if (erpSalesOrder)
{
    txtDiscount.Enabled = false;
    curCalculatedPrice.Enabled = false;
}
btnOK.Visible = !closed;

var salesOrderItem = BindingSource.Current as ISalesOrderItem;
lstbxPriceLevel.Items.Clear();
if (salesOrderItem.Product != null)
{
	lstbxPriceLevel.Items.Add(string.Empty);
    foreach (IProductProgram priceLevel in salesOrderItem.Product.ProductProgram)
    {
        var item = new ListItem {Text = priceLevel.Program, Value = priceLevel.Program};
        lstbxPriceLevel.Items.Add(item);
    }
    lstbxPriceLevel.SelectedValue = salesOrderItem.Program;
}

QFSDataGrid.ConditionalWhere:
function() {
    var clientContextService = Sage.Services.getService('ClientContextService');
    var itemId = clientContextService != null && clientContextService.containsKey('SOITEMID') ? clientContextService.getValue("SOITEMID") : null;
    return dojo.string.substitute("SOItemDistributedCharge.Id eq '${0}'", [itemId]);
}

CSharpSnippetActionItem.CSharpCodeSnippet:
ISalesOrderItem salesOrderItem = BindingSource.Current as ISalesOrderItem;
var orderItemChangeState = salesOrderItem as Sage.Platform.ChangeManagement.IChangedState;
var shouldSave = true;
if (orderItemChangeState != null)
{
    Sage.Platform.ChangeManagement.ChangeSet changes = orderItemChangeState.GetChangedState();
    if (changes.HasMemberChange("Quantity") || changes.HasMemberChange("UnitOfMeasure"))
    {
        salesOrderItem.SalesOrder.SyncStatus = Saleslogix.Integration.BOE.Common.Constants.SyncStatus.ChangesPending;
        shouldSave = false;
        salesOrderItem.SalesOrder.Save();
    }
}
if (shouldSave)
{
    salesOrderItem.Save();
}
